package com.nexus.rickandmortycompose.ui.composablesimport androidx.compose.foundation.layout.PaddingValuesimport androidx.compose.material3.ExperimentalMaterial3Apiimport androidx.compose.material3.Scaffoldimport androidx.compose.runtime.Composableimport androidx.navigation.NavHostControllerimport com.nexus.rickandmortycompose.screens.appCurrentDestinationAsStateimport com.nexus.rickandmortycompose.screens.destinations.Destinationimport com.nexus.rickandmortycompose.screens.startAppDestinationimport com.ramcosta.composedestinations.spec.Route@OptIn(ExperimentalMaterial3Api::class)@Composablefun BaseScaffold(    startRoute: Route,    navController: NavHostController,    topBar: @Composable () -> Unit,    bottomNavBar: @Composable (Destination) -> Unit,    content: @Composable (PaddingValues) -> Unit) {    val destination = navController.appCurrentDestinationAsState().value        ?: startRoute.startAppDestination    Scaffold(        topBar = topBar,        bottomBar = {            bottomNavBar(destination)        },        content = content    )}